mapper.update:
update agri_city_sample_test_details
sample_code = #{sampleCode}, veg_fru_name = #{vegFruName}, sampling_location = #{samplingLocation}, detect_location = #{detectLocation}, sampling_quantity = #{samplingQuantity}, sampling_base = #{samplingBase}, sampling_stage_type = #{samplingStageType}, sampling_date = #{samplingDate}, china_standard = #{chinaStandard}, cac_standard = #{cacStandard}, japan_standard = #{japanStandard}, eu_standard = #{euStandard}, us_standard = #{usStandard}, korea_standard = #{koreaStandard}, created_at = #{createdAt}, sampling_location_province= #{samplingLocationProvince}, sampling_location_city=#{samplingLocationCity}, sampling_location_county=#{samplingLocationCounty}, enterprise_name=#{enterpriseName}, enterprise_attribute=#{enterpriseAttribute}, enterprise_credit_id_code=#{enterpriseCreditIdCode}, tracing_province=#{tracingProvince}, tracing_city=#{tracingCity}, tracing_county=#{tracingCounty}, tracing_area=#{tracingArea}, all_district_code=#{allDistrictCode},
where city_sample_test_details_id = #{citySampleTestDetailsId}

mapper.select:
select veg_fru_name
from agri_city_sample_test_details
where city_sample_test_details_id = #{arg0}

select sampling_stage_type
from agri_city_sample_test_details
where city_sample_test_details_id = #{arg0}

SELECT agri_city_sample_test_details.* FROM agri_city_sample_test_details
LEFT JOIN agri_pesticide_det_result ON
agri_city_sample_test_details.city_sample_test_details_id=agri_pesticide_det_result.city_sample_test_details_id
LEFT JOIN agri_vegetable_fruit ON agri_city_sample_test_details.veg_fru_name = agri_vegetable_fruit.veg_fru_name


    and sample_code = #{agriCitySampleTestDetails.sampleCode}
    and agri_city_sample_test_details.veg_fru_name like
        concat('%', #{agriCitySampleTestDetails.vegFruName}, '%')


    and sampling_location like concat('%',
        #{samplingLocation}, '%')


    and detect_location like concat('%',
        #{agriCitySampleTestDetails.detectLocation}, '%')

    and sampling_stage_type like concat('%',
        #{agriCitySampleTestDetails.samplingStageType}, '%')

    and sampling_date = #{agriCitySampleTestDetails.samplingDate}
    and china_standard = #{agriCitySampleTestDetails.chinaStandard}
    and cac_standard = #{agriCitySampleTestDetails.cacStandard}
    and japan_standard = #{agriCitySampleTestDetails.japanStandard}
    and eu_standard = #{agriCitySampleTestDetails.euStandard}
    and us_standard = #{agriCitySampleTestDetails.usStandard}
    and korea_standard = #{agriCitySampleTestDetails.koreaStandard}

    and
        sampling_location_province = #{agriCitySampleTestDetails.samplingLocationProvince}

    and sampling_location_city =
        #{agriCitySampleTestDetails.samplingLocationCity}

    and sampling_location_county =
        #{agriCitySampleTestDetails.samplingLocationCounty}

    and enterprise_attribute =
        #{agriCitySampleTestDetails.enterpriseAttribute}

    and enterprise_name = #{agriCitySampleTestDetails.enterpriseName}


    and enterprise_credit_id_code =
        #{agriCitySampleTestDetails.enterpriseCreditIdCode}

    and tracing_province = #{agriCitySampleTestDetails.tracingProvince}
    and tracing_city = #{agriCitySampleTestDetails.tracingCity}
    and tracing_county = #{agriCitySampleTestDetails.tracingCounty}
    and tracing_area = #{agriCitySampleTestDetails.tracingArea}

    and veg_fru_type = #{agriCitySampleTestDetails.vegFruType}
    and user_name = #{agriCitySampleTestDetails.userName}
    and all_district_code = #{agriCitySampleTestDetails.allDistrictCode}
    and pesticide_name = #{agriCitySampleTestDetails.pesticideName}
    and  sampling_type= #{samplingType}




        AND DATE(agri_city_sample_test_details.created_at) = #{agriCitySampleTestDetails.createdAt}



        AND date_format(sampling_date,'%y%m%d') >= date_format(#{agriCitySampleTestDetails.params.beginTime},'%y%m%d')


        AND date_format(sampling_date,'%y%m%d') <= date_format(#{agriCitySampleTestDetails.params.endTime},'%y%m%d')


GROUP BY agri_city_sample_test_details.city_sample_test_details_id

SELECT t_address_city.name AS samplingLocation,
-- 蔬菜统计 SUM(CASE WHEN vf.veg_fru_type = '蔬菜' THEN 1 ELSE 0 END) AS vegSamplingCount, SUM(CASE WHEN vf.veg_fru_type = '蔬菜' AND acstd.city_sample_test_details_id NOT IN ( SELECT acstd.city_sample_test_details_id FROM agri_city_sample_test_details acstd LEFT JOIN agri_vegetable_fruit vf ON acstd.veg_fru_name = vf.veg_fru_name LEFT JOIN agri_pesticide_det_result pdr ON acstd.city_sample_test_details_id = pdr.city_sample_test_details_id LEFT JOIN agri_pesticide_residue_standard prs ON pdr.pesticide_name = prs.pesticide_name AND vf.veg_fru_name = prs.veg_fru_name WHERE pdr.pesticide_det_value > prs.standard_value ) THEN 1 ELSE 0 END) AS vegPassCount, CASE WHEN SUM(CASE WHEN vf.veg_fru_type = '蔬菜' THEN 1 ELSE 0 END) = 0 THEN 0 ELSE ROUND( SUM(CASE WHEN vf.veg_fru_type = '蔬菜' AND acstd.city_sample_test_details_id NOT IN ( SELECT acstd.city_sample_test_details_id FROM agri_city_sample_test_details acstd LEFT JOIN agri_vegetable_fruit vf ON acstd.veg_fru_name = vf.veg_fru_name LEFT JOIN agri_pesticide_det_result pdr ON acstd.city_sample_test_details_id = pdr.city_sample_test_details_id LEFT JOIN agri_pesticide_residue_standard prs ON pdr.pesticide_name = prs.pesticide_name AND vf.veg_fru_name = prs.veg_fru_name WHERE pdr.pesticide_det_value > prs.standard_value ) THEN 1 ELSE 0 END) / SUM(CASE WHEN vf.veg_fru_type = '蔬菜' THEN 1 ELSE 0 END) * 100, 2 ) END AS vegPassRate,
-- 水果统计 SUM(CASE WHEN vf.veg_fru_type = '水果' THEN 1 ELSE 0 END) AS fruSamplingCount, SUM(CASE WHEN vf.veg_fru_type = '水果' AND acstd.city_sample_test_details_id NOT IN ( SELECT acstd.city_sample_test_details_id FROM agri_city_sample_test_details acstd LEFT JOIN agri_vegetable_fruit vf ON acstd.veg_fru_name = vf.veg_fru_name LEFT JOIN agri_pesticide_det_result pdr ON acstd.city_sample_test_details_id = pdr.city_sample_test_details_id LEFT JOIN agri_pesticide_residue_standard prs ON pdr.pesticide_name = prs.pesticide_name AND vf.veg_fru_name = prs.veg_fru_name WHERE pdr.pesticide_det_value > prs.standard_value ) THEN 1 ELSE 0 END) AS fruPassCount, CASE WHEN SUM(CASE WHEN vf.veg_fru_type = '水果' THEN 1 ELSE 0 END) = 0 THEN 0 ELSE ROUND( SUM(CASE WHEN vf.veg_fru_type = '水果' AND acstd.city_sample_test_details_id NOT IN ( SELECT acstd.city_sample_test_details_id FROM agri_city_sample_test_details acstd LEFT JOIN agri_vegetable_fruit vf ON acstd.veg_fru_name = vf.veg_fru_name LEFT JOIN agri_pesticide_det_result pdr ON acstd.city_sample_test_details_id = pdr.city_sample_test_details_id LEFT JOIN agri_pesticide_residue_standard prs ON pdr.pesticide_name = prs.pesticide_name AND vf.veg_fru_name = prs.veg_fru_name WHERE pdr.pesticide_det_value > prs.standard_value ) THEN 1 ELSE 0 END) / SUM(CASE WHEN vf.veg_fru_type = '水果' THEN 1 ELSE 0 END) * 100, 2 ) END AS fruPassRate,
-- 茶叶统计 SUM(CASE WHEN vf.veg_fru_type = '茶叶' THEN 1 ELSE 0 END) AS teaSamplingCount, SUM(CASE WHEN vf.veg_fru_type = '茶叶' AND acstd.city_sample_test_details_id NOT IN ( SELECT acstd.city_sample_test_details_id FROM agri_city_sample_test_details acstd LEFT JOIN agri_vegetable_fruit vf ON acstd.veg_fru_name = vf.veg_fru_name LEFT JOIN agri_pesticide_det_result pdr ON acstd.city_sample_test_details_id = pdr.city_sample_test_details_id LEFT JOIN agri_pesticide_residue_standard prs ON pdr.pesticide_name = prs.pesticide_name AND vf.veg_fru_name = prs.veg_fru_name WHERE pdr.pesticide_det_value > prs.standard_value ) THEN 1 ELSE 0 END) AS teaPassCount, CASE WHEN SUM(CASE WHEN vf.veg_fru_type = '茶叶' THEN 1 ELSE 0 END) = 0 THEN 0 ELSE ROUND( SUM(CASE WHEN vf.veg_fru_type = '茶叶' AND acstd.city_sample_test_details_id NOT IN ( SELECT acstd.city_sample_test_details_id FROM agri_city_sample_test_details acstd LEFT JOIN agri_vegetable_fruit vf ON acstd.veg_fru_name = vf.veg_fru_name LEFT JOIN agri_pesticide_det_result pdr ON acstd.city_sample_test_details_id = pdr.city_sample_test_details_id LEFT JOIN agri_pesticide_residue_standard prs ON pdr.pesticide_name = prs.pesticide_name AND vf.veg_fru_name = prs.veg_fru_name WHERE pdr.pesticide_det_value > prs.standard_value ) THEN 1 ELSE 0 END) / SUM(CASE WHEN vf.veg_fru_type = '茶叶' THEN 1 ELSE 0 END) * 100, 2 ) END AS teaPassRate,
-- 总数统计 SUM(CASE WHEN vf.veg_fru_type IN ('蔬菜', '水果', '茶叶') THEN 1 ELSE 0 END) AS allSamplingCount, SUM(CASE WHEN vf.veg_fru_type IN ('蔬菜', '水果', '茶叶') AND acstd.city_sample_test_details_id NOT IN ( SELECT acstd.city_sample_test_details_id FROM agri_city_sample_test_details acstd LEFT JOIN agri_vegetable_fruit vf ON acstd.veg_fru_name = vf.veg_fru_name LEFT JOIN agri_pesticide_det_result pdr ON acstd.city_sample_test_details_id = pdr.city_sample_test_details_id LEFT JOIN agri_pesticide_residue_standard prs ON pdr.pesticide_name = prs.pesticide_name AND vf.veg_fru_name = prs.veg_fru_name WHERE pdr.pesticide_det_value > prs.standard_value ) THEN 1 ELSE 0 END) AS allPassCount, CASE WHEN SUM(CASE WHEN vf.veg_fru_type IN ('蔬菜', '水果', '茶叶') THEN 1 ELSE 0 END) = 0 THEN 0 ELSE ROUND( SUM(CASE WHEN vf.veg_fru_type IN ('蔬菜', '水果', '茶叶') AND acstd.city_sample_test_details_id NOT IN ( SELECT acstd.city_sample_test_details_id FROM agri_city_sample_test_details acstd LEFT JOIN agri_vegetable_fruit vf ON acstd.veg_fru_name = vf.veg_fru_name LEFT JOIN agri_pesticide_det_result pdr ON acstd.city_sample_test_details_id = pdr.city_sample_test_details_id LEFT JOIN agri_pesticide_residue_standard prs ON pdr.pesticide_name = prs.pesticide_name AND vf.veg_fru_name = prs.veg_fru_name WHERE pdr.pesticide_det_value > prs.standard_value ) THEN 1 ELSE 0 END) / SUM(CASE WHEN vf.veg_fru_type IN ('蔬菜', '水果', '茶叶') THEN 1 ELSE 0 END) * 100, 2 ) END AS allPassRate FROM agri_city_sample_test_details acstd LEFT JOIN agri_vegetable_fruit vf ON acstd.veg_fru_name = vf.veg_fru_name LEFT JOIN t_address_city ON acstd.sampling_location_city = t_address_city.name -- 关联到城市名称
and sample_code = #{agriCitySampleTestDetails.sampleCode} and agri_city_sample_test_details.veg_fru_name like concat('%', #{agriCitySampleTestDetails.vegFruName}, '%')
and sampling_location like concat('%', #{samplingLocation}, '%')
and detect_location like concat('%', #{agriCitySampleTestDetails.detectLocation}, '%')
and sampling_stage_type like concat('%', #{agriCitySampleTestDetails.samplingStageType}, '%')
and sampling_date = #{agriCitySampleTestDetails.samplingDate} and china_standard = #{agriCitySampleTestDetails.chinaStandard} and cac_standard = #{agriCitySampleTestDetails.cacStandard} and japan_standard = #{agriCitySampleTestDetails.japanStandard} and eu_standard = #{agriCitySampleTestDetails.euStandard} and us_standard = #{agriCitySampleTestDetails.usStandard} and korea_standard = #{agriCitySampleTestDetails.koreaStandard}
and sampling_location_province = #{agriCitySampleTestDetails.samplingLocationProvince}
and sampling_location_city = #{agriCitySampleTestDetails.samplingLocationCity}
and sampling_location_county = #{agriCitySampleTestDetails.samplingLocationCounty}
and enterprise_attribute = #{agriCitySampleTestDetails.enterpriseAttribute}
and enterprise_name = #{agriCitySampleTestDetails.enterpriseName}
and enterprise_credit_id_code = #{agriCitySampleTestDetails.enterpriseCreditIdCode}
and tracing_province = #{agriCitySampleTestDetails.tracingProvince} and tracing_city = #{agriCitySampleTestDetails.tracingCity} and tracing_county = #{agriCitySampleTestDetails.tracingCounty} and tracing_area = #{agriCitySampleTestDetails.tracingArea}
and veg_fru_type = #{agriCitySampleTestDetails.vegFruType} and user_name = #{agriCitySampleTestDetails.userName} and all_district_code = #{agriCitySampleTestDetails.allDistrictCode} and pesticide_name = #{agriCitySampleTestDetails.pesticideName} and sampling_type= #{agriCitySampleTestDetails.samplingType}
AND DATE(agri_city_sample_test_details.created_at) = #{agriCitySampleTestDetails.createdAt}
AND date_format(sampling_date,'%y%m%d') >= date_format(#{agriCitySampleTestDetails.params.beginTime},'%y%m%d')
AND date_format(sampling_date,'%y%m%d') <= date_format(#{agriCitySampleTestDetails.params.endTime},'%y%m%d')
GROUP BY t_address_city.name ORDER BY t_address_city.name

