mapper.update:
update out_veg_fru_inventory
vegetable_seq_no = #{vegetableSeqNo}, vegetable_detail_name = #{vegetableDetailName}, vegetable_quantity = #{vegetableQuantity}, fruit_seq_no = #{fruitSeqNo}, fruit__detail_name = #{fruitDetailName}, fruit_quantity = #{fruitQuantity}, created_date = #{createdDate},
where veg_fru_inventory_id = #{vegFruInventoryId}

mapper.select:
SELECT
avf.veg_fru_name AS NAME,
'蔬菜' AS TYPE,
COUNT(acstd.city_sample_test_details_id) AS Quantity
FROM
agri_city_sample_test_details acstd
JOIN
agri_vegetable_fruit avf ON acstd.veg_fru_name = avf.veg_fru_name
where
avf.veg_fru_type = '蔬菜'

 and acstd.sampling_location_province = #{samplingLocationProvince}
 and acstd.sampling_location_city = #{samplingLocationCity}
 and acstd.sampling_location_county = #{samplingLocationCounty}
 and acstd.enterprise_attribute = #{enterpriseAttribute}
 and acstd.enterprise_credit_id_code = #{enterpriseCreditIdCode}
 and acstd.tracing_province = #{tracingProvince}
 and acstd.tracing_city = #{tracingCity}
 and acstd.tracing_county = #{tracingCounty}
 and acstd.tracing_area = #{tracingArea}



        AND date_format(acstd.sampling_date,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


        AND date_format(acstd.sampling_date,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


GROUP BY
avf.veg_fru_name

UNION ALL

SELECT
avf.veg_fru_name AS NAME,
'水果' AS TYPE,
COUNT(acstd.city_sample_test_details_id) AS Quantity
FROM
agri_city_sample_test_details acstd
JOIN
agri_vegetable_fruit avf ON acstd.veg_fru_name = avf.veg_fru_name
WHERE
avf.veg_fru_type = '水果'

 and acstd.sampling_location_province = #{samplingLocationProvince}
 and acstd.sampling_location_city = #{samplingLocationCity}
 and acstd.sampling_location_county = #{samplingLocationCounty}
 and acstd.enterprise_attribute = #{enterpriseAttribute}
 and acstd.enterprise_credit_id_code = #{enterpriseCreditIdCode}
 and acstd.tracing_province = #{tracingProvince}
 and acstd.tracing_city = #{tracingCity}
 and acstd.tracing_county = #{tracingCounty}
 and acstd.tracing_area = #{tracingArea}



    AND date_format(acstd.sampling_date,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


    AND date_format(acstd.sampling_date,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

GROUP BY
avf.veg_fru_name



UNION ALL
SELECT
avf.veg_fru_name AS NAME,
'茶叶' AS TYPE,
COUNT(acstd.city_sample_test_details_id) AS Quantity
FROM
agri_city_sample_test_details acstd
JOIN
agri_vegetable_fruit avf ON acstd.veg_fru_name = avf.veg_fru_name
WHERE
avf.veg_fru_type = '茶叶'

 and acstd.sampling_location_province = #{samplingLocationProvince}
 and acstd.sampling_location_city = #{samplingLocationCity}
 and acstd.sampling_location_county = #{samplingLocationCounty}
 and acstd.enterprise_attribute = #{enterpriseAttribute}
 and acstd.enterprise_credit_id_code = #{enterpriseCreditIdCode}
 and acstd.tracing_province = #{tracingProvince}
 and acstd.tracing_city = #{tracingCity}
 and acstd.tracing_county = #{tracingCounty}
 and acstd.tracing_area = #{tracingArea}



    AND date_format(acstd.sampling_date,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


    AND date_format(acstd.sampling_date,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

GROUP BY
avf.veg_fru_name
